import json# python 转 jsonstu1 = {    'name': 'xiaoming',    'age': 20,    'height': 170,    'score': {        'yuwen': 89,        'shuxue': 99    },    'books': ['中文', 'python七天成功']}json_stu1 = json.dumps(stu1)print(type(json_stu1), json_stu1)with open('stu.json', 'w', encoding='utf8') as f:    json.dump(stu1, f)# json 转 pythonjson_str = ''''''py_obj = json.loads(json_str)with open('stu.json', 'r', encoding='utf8') as f:    json.load(f)# 对象 转 jsonclass Student(object):    def __init__(self, name, age):        self.name = name        self.age = agedef convert_to_builtin_type(obj):    return obj.__dict__print(json.dumps(Student('name', 26), default=convert_to_builtin_type))# json 转 对象json_str = ''''''def convert_to_obj_type(obj):    # inst = eval('Student(d["name"], d["age"])')    # module = __import__(module_name)    class_ = getattr(__name__, 'Student')    args = dict((key.encode('utf8'), value) for key, value in obj.items())    inst = class_(**args)    return instprint(json.loads(json_str, object_hook=convert_to_obj_type))# json 转换参数说明# skipkeys：False# ensure_ascii：True# indent：None# sort_keys: False# default: None# separators: None, (item_separator, key_separator), (', ', ': ')