import sysprint(type(sys.argv))       # 获取命令行参数，print(type(sys.argv[0]))    # 第一个为 当前文件名if len(sys.argv) > 2:    print('pass')else:    print('缺少参数')# sys.exit(0)     # 同于 exit()print(sys.modules)      # 打印已加载Python模块def dump(module):    result = module + ' =>'    if module in sys.builtin_module_names:        result += ' <BUILTIN>'    else:        try:            module = __import__(module)            result += ' ' + module.__file__        except:            result += ' 模块名无效'    return resultprint(dump("os"))print(dump("sys"))print(dump("string"))print(dump("json"))print(dump("xml"))print(dump("huice"))