import xlrddata = xlrd.open_workbook('test.xls')print(type(data))   # 根节点print(type(data.sheets()))  # 获取全部sheetfor sheet in data.sheets():     # 遍历sheet    print(type(sheet))sheet = data.sheet_by_index(0)  # 获取指定下标sheetprint(sheet.row_values(0))  # 获取sheet第1行的值print(sheet.col_values(0))  # 获取sheet第1列的值sheet = data.sheet_by_name('Case01')    # 获取指定名称sheetprint(sheet.cell(0, 0))   # 获取指定cell单元print(sheet.cell(0, 0).value)     # 获取cell单元内容print(sheet.nrows)      # 获取sheet总行数print(sheet.ncols)      # 获取sheet总列数import xlwtxls = xlwt.Workbook()sheet = xls.add_sheet('sample')sheet.write(0, 0, 'huice')sheet.write(0, 1, 'huice.com')xls.save('sample.xls')